const express = require('express');
const path = require('path');
const multer = require('multer');
const mysql = require('mysql2/promise');

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: true }));

const upload = multer({ dest: path.join(__dirname, 'public/uploads/') });

(async function () {
  const connection = await mysql.createConnection({
    host: 'localhost', // Replace with your DB host
    user: 'root', // Replace with your DB username
    password: 'Tsire@2004',            
    database: 'medical_aid_details',
  });

  // Serve the static form
  app.use(express.static(path.join(__dirname, 'public')));

  // Handle form submission
  app.post('/submit-form', upload.single('medicalAidCard'), async (req, res) => {
    const { firstName, lastName, dob, phoneNumber, medicalAidNumber } = req.body;
    const medicalAidCard = req.file ? req.file.filename : null;

    // Insert form data into the MySQL database
    const sql = `INSERT INTO medical_aid_details (first_name, last_name, dob, phone_number, medical_aid_number, medical_aid_card)
                 VALUES (?, ?, ?, ?, ?, ?)`;
    try {
      const [result] = await connection.execute(sql, [firstName, lastName, dob, phoneNumber, medicalAidNumber, medicalAidCard]);
      console.log('Data inserted successfully:', result);
      res.send('Form submitted successfully!');
    } catch (err) {
      console.error('Error inserting data into database:', err);
      res.status(500).send('Database error.');
    }
  });

  // Start the server
  const port = process.env.PORT || 3000;
  app.listen(port, () => {
    console.log(`Server running on port ${port}`);
  });
})();

